(function executeRule(current, previous /*null when async*/) {
//Jeito novo de se fazer, usando Script Include
	// Instancia a classe NeedItUtils, chama o método IsDatePass e passa o valor u_when_needed
	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);
	// Se o método isDatePast retornar true, a data estará no passado.
	if(isPast == true){
		gs.addErrorMessage("A data não pode estar no passado. Portanto, sua solicitação não foi salva no banco.");
		current.setAbortAction(true);
	}


	//Verificar se a data é igual a de hoje - Metodo novo com Script Include
	var isToday = niutils.isDateToday(current.u_when_needed);
	// Se o método isDateToday retornar true, a data é de hoej
	if(isToday == true){
		gs.addErrorMessage("Você não pode fazer uma solicitação na data de hoje. Portanto, sua solicitação não foi salva no banco.");
		current.setAbortAction(true);
	}

/* //Jeito antigo de se fazer - Usando Bussiness Rules
