sys_script_client.script:
function onLoad(){
	//Gerar um alerta quando o formulário for carregado
	alert('Obrigada por acessar este formulário. Assim que você fechar este alerta,  poderá interagir com o formulário');
}

function onSubmit(){
	//Gerar um alerta de agradecimento por ter enviado o formulário
	alert('Obrigada por nos enviar o formulário!');
}

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//Gera um alerta informando o antigo valor e o novo valor alterado.
	alert('Você alterou a pequena descrição de ' + oldValue +" para " + newValue + ".");
}
