sys_script_include.script:
var GetEmailAddress = Class.create();
// Extende a classe global.AbstractAjaxProcessor
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define a função getEmail
	// Cria a GlideRecord para a tabela de usuário
	// Usa a sysparm_userID passado do lado do cliente para recuperar um registro na tabela usuários
	//Retorna o endereço de email para o registro solicitado
getEmail: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},
	type: 'GetEmailAddress'
});
