sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast : function(strDate){
	// Crie objetos GlideDateTime para a data atual e a data passada
	var rigthNow = new GlideDateTime();
	var testDate = new GlideDateTime(strDate);

	// Se a data do teste for anterior agora, retorne true, caso contrário, retorne false
	if(testDate.before(rigthNow))
	{
		return true;
	}
	else{
			return false;
		}

	},

	isDateToday : function(strDate){
	// Crie objetos GlideDateTime para a data atual e a data passada
		var rigthNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
	// Obtenha a parte da data do rightnow e testdate (sem hora)
		var today = rigthNow.getLocalDate();
		var isToday = testDate.getLocalDate();

		//Se a data do teste for igual a de hoje, retorne true, caso contrário, retorne false
		if(today.compareTo(isToday) == 0){
			return true;
		}
		else{
			return false;
		}
	},
    type: 'NeedItUtils'
};
