sysauto_script.script:
// Recupera o valor da propriedade do aplicativo x_58872_needit.autoCloseOverdue
	var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

// Consulta os registros da tarefa NeedIt no banco de dados com o valor do campo "Due date"
// mais antigos do número de dias definido na propriedade do aplicativo
// x_58872_needit.autoCloseOverdue.
// Somente retorna os registros NeedIt Task que têm um valor State "Open" ou "Work in progress"
// em andamento. Feche as tarefas NeedIt e atualize as notas do trabalho da tarefa NeedIt.

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks.addQuery('due_date','<=',gs.daysAgo(howLongToClose));
	overdueNITasks.addQuery('state','<',3);
	overdueNITasks.query();

// Escreva uma mensagem de log para cada registro de tarefa NeedIt muito atrasado.
// Adicione uma nota ao campo Notas de trabalho. Altere o State para "Closed Incomplete".
// Atualize o registro da tarefa NeedIt.
while(overdueNITasks.next()){
	gs.info("Fechar automaticamente o registro da tarefa NeedIt =" + overdueNITasks.number);
	overdueNITasks.work_notes = "O registro foi fechado porque estava muito longe da data de vencimento.";
		overdueNITasks.state = 4;
		overdueNITasks.update();


}
