sysauto_script.script:
//Obter data e hora atual
var rightnow = new GlideDateTime();

//Consulta no banco de dados e pega as tarefas com datas de vencimento de agora até 24hrs

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
dueSoon.addQuery('due_date', '>=', rightnow);
dueSoon.addQuery('due_date','<', gs.hoursAgo(-24));
dueSoon.addQuery('State','!=',3);
dueSoon.query();

//Informa quais sao as tarefas por meio de mensagem
while(dueSoon.next()){
gs.info("As tarefas com datas de agora e até 24hrs são: " + dueSoon.number);
gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoon,dueSoon.number,gs.getUserName());
}
